import {Form, Button} from 'react-bootstrap'
import React from "react";
import { CSSGrid, layout } from 'react-stonecutter';
import logo from '../images/logo.png'


class HomeView extends React.Component{
    constructor(){
        super();
        this.state = {
            name: ""
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event){
        const {name,value} = event.target
        this.setState({
            [name]: value
        })
    }

    render(){
        return(
            <div>
                <div style={{justifyContent: "center", alignItems: "center", display: "flex", marginTop: "15%"}}>
                    {/* <img style={{width: "20%"}} src={logo} alt="" /> */}
                    <Form>
                        <Form.Group controlId="formBasicEmail">
                            <Form.Label>Searching</Form.Label>
                            <Form.Control name="name" value={this.state.name} onChange={this.handleChange} size="lg" type="text" placeholder="Large text" />
                            <Form.Text className="text-muted">
                            We'll never share your email with anyone else.
                            </Form.Text>
                        </Form.Group>

                        <Button variant="primary" type="submit">
                        <a href={'/ListView/'+this.state.name}>Search</a>
                        </Button>
                    </Form>
                </div>
            </div>
        )
    }
}

export default HomeView
