import React from 'react'

var Circle = require('rc-progress').Circle;

class Circle_c extends React.Component{
    constructor(){
        super();
        this.state = {
            color: "",
            percent: ""
        }
    }
    componentDidMount(){
        console.log(this.props.vote_average)
        this.setState({
            percent: this.props.vote_average*10
        })
        if(this.props.vote_average <= 4){
            this.setState({
                color:"#ff0000"
            })
        }else if(this.props.vote_average <= 6 && this.props.vote_average > 4){
            this.setState({
                color:"#E8E83A"
            })
        }else{
            this.setState({
                color:"#7CFC00"
            })
        }
    }

    render(){


        return(
            <div>
                <Circle percent={this.state.percent} style={{width:"50px", marginLeft:"20px"}} strokeWidth="15" strokeColor={this.state.color} />
            </div>
        )
    }
}

export default Circle_c
