import {Form, Row, Col, Container} from 'react-bootstrap'
import React from "react";
import axios from 'axios';

const DemoBox = props => <p className={`height-${props.value}`}>{props.children}</p>;

class HomeView extends React.Component{
    constructor(){
        super();
        this.state = {
            name: "",
            results: [],
            name: []
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event){
        const {name,value} = event.target
        this.setState({
            [name]: value
        })
    }

    componentDidMount(){
        var r = []
        var rn = []
        axios.get("https://api.themoviedb.org/3/movie/top_rated?api_key=6aaf32f1d12ae102cb4ee6849f3d5ac0&language=en-US&page=1")
            .then( data => {

                data.data.results.map(d => {
                    // var ran = parseInt(Math.random()*(19+1),10);
                    // console.log(d.poster_path)
                    r.push(d.poster_path)
                    rn.push(d.title)
                })
                this.setState({
                    results: r,
                    name: rn
                })
            })
    }

    render(){
        return(
            <div>
                <Container>
                    <Row>
                        {
                            console.log(this.state.results[0])
                        }
                        <Col md={3}>
                        <img
                            width={200}
                            alt="logo"
                            src={"https://image.tmdb.org/t/p/original"+this.state.results[0]}
                        /><br/>Master
                        </Col>
                        <Col md={{ span: 3, offset: 1.5 }}>
                        <img
                            width={150}
                            alt="logo"
                            src={"https://image.tmdb.org/t/p/original/s1cVTQEZYn4nSjZLnFbzLP0j8y2.jpg"}
                        /></Col>
                        <Col md={{ span: 3, offset: 4.5 }}>
                        <img
                            width={200}
                            alt="logo"
                            src={"https://image.tmdb.org/t/p/original/s1cVTQEZYn4nSjZLnFbzLP0j8y2.jpg"}
                        /></Col>
                        <Col md={{ span: 3, offset: 7.5 }}>
                        <img
                            width={150}
                            alt="logo"
                            src={"https://image.tmdb.org/t/p/original/s1cVTQEZYn4nSjZLnFbzLP0j8y2.jpg"}
                        /></Col>
                    </Row>
                    <Row>
                        <Col style={{marginTop: "50px"}} md={{ span: 3, offset: 1.5 }}>
                        <img
                            width={150}
                            alt="logo"
                            src={"https://image.tmdb.org/t/p/original/s1cVTQEZYn4nSjZLnFbzLP0j8y2.jpg"}
                        /></Col>
                        <Col md={{ span: 3, offset: 4.5 }}>
                        <img
                            width={200}
                            alt="logo"
                            src={"https://image.tmdb.org/t/p/original/s1cVTQEZYn4nSjZLnFbzLP0j8y2.jpg"}
                        /></Col>
                        <Col style={{marginTop: "50px"}} md={{ span: 3, offset: 7.5 }}>
                        <img
                            width={150}
                            alt="logo"
                            src={"https://image.tmdb.org/t/p/original/s1cVTQEZYn4nSjZLnFbzLP0j8y2.jpg"}
                        /></Col>
                        <Col md={{ span: 3, offset: 7.5 }}>
                        <img
                            width={200}
                            alt="logo"
                            src={"https://image.tmdb.org/t/p/original/s1cVTQEZYn4nSjZLnFbzLP0j8y2.jpg"}
                        /></Col>
                    </Row>
                </Container>
            </div>
        )
    }
}

export default HomeView
