import React from 'react'
import { List, Avatar, Icon } from 'antd';
import axios from 'axios'


// for (let i = 0; i < 23; i++) {
//   listData.push({
//     href: 'http://ant.design',
//     title: `ant design part ${i}`,
//     avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
//     description:
//       'Ant Design, a design language for background applications, is refined by Ant UED Team.',
//     content:
//       'We supply a series of design principles, practical patterns and high quality design resources (Sketch and Axure), to help people create their product prototypes beautifully and efficiently.',
//   });
// }

const IconText = ({ type, text }) => (
    <span>
      <Icon type={type} style={{ marginRight: 8 }} />
      {text}
    </span>
  );



class ListView extends React.Component{
    constructor(){
        super();
        this.state={
            overview:"",
            results:{},
            listData:[]
        }
    }

    componentDidMount(){
        var name = this.props.match.params.name;
        axios.get(`https://api.themoviedb.org/3/search/movie?api_key=6aaf32f1d12ae102cb4ee6849f3d5ac0&query=${name}`)
            // .then(response => response.json())
            .then(data => {
                // console.log(data.data.results[0].overview)
                console.log(data.data.results)
                this.results=data.data
            })

            //render list
            listData = []
            for (let i = 0; i < this.state.results.total_pages; i++) {
                listData.push({
                  href: 'http://ant.design',
                  title: `ant design part ${i}`,
                  avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
                  description:this.state.results.results[i].overview,
                  content:
                    'We supply a series of design principles, practical patterns and high quality design resources (Sketch and Axure), to help people create their product prototypes beautifully and efficiently.',
                });
              }

            console.log(this.setState)
    }

    render(){
        return(
            <div>
                <div style={{marginTop: "5%", marginRight: '10%', marginLeft: '10%'}}>
                    <List
                        itemLayout="vertical"
                        size="large"
                        pagination={{
                        onChange: page => {
                            console.log(page);
                        },
                        pageSize: 3,
                        }}
                        dataSource={this.state.listData}
                        footer={
                        <div>
                            <b>ant design</b> footer part
                        </div>
                        }
                        renderItem={item => (
                        <List.Item
                            key={item.title}
                            actions={[
                            <IconText type="star-o" text="156" key="list-vertical-star-o" />,
                            <IconText type="like-o" text="156" key="list-vertical-like-o" />,
                            <IconText type="message" text="2" key="list-vertical-message" />,
                            ]}
                            extra={
                            <img
                                width={272}
                                alt="logo"
                                src="https://gw.alipayobjects.com/zos/rmsportal/mqaQswcyDLcXyDKnZfES.png"
                            />
                            }
                        >
                            <List.Item.Meta
                            avatar={<Avatar src={item.avatar} />}
                            title={<a href={item.href}>{item.title}</a>}
                            description={item.description}
                            />
                            {item.content}
                        </List.Item>
                        )}
                    />
                </div>
            </div>
        )
    }
}

export default ListView
