import React from 'react'
import { List, Icon } from 'antd';
import axios from 'axios'
import Circle_c from '../components/Circle_c'

// for (let i = 0; i < 23; i++) {
//   listData.push({
//     href: 'http://ant.design',
//     title: `ant design part ${i}`,
//     avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
//     description:
//       'Ant Design, a design language for background applications, is refined by Ant UED Team.',
//     content:
//       'We supply a series of design principles, practical patterns and high quality design resources (Sketch and Axure), to help people create their product prototypes beautifully and efficiently.',
//   });
// }
var Circle = require('rc-progress').Circle;



class ListView extends React.Component{
    constructor(){
        super();
        this.state={
            overview:"",
            results:{},
            listData:[]
        }
    }

    componentDidMount(){
        var name = this.props.match.params.name;
        const ldd = []
        axios.get(`https://api.themoviedb.org/3/search/movie?api_key=6aaf32f1d12ae102cb4ee6849f3d5ac0&query=${name}`)
            // .then(response => response.json())
            .then(data => {

                console.log(data.data)

                data.data.results.map(d => {
                    ldd.push({
                        href: 'http://ant.design',
                        title: d.title,
                        // avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
                        description: d.overview,
                        release_date: d.release_date,
                        image: "https://image.tmdb.org/t/p/original"+d.poster_path,
                        vote_average: d.vote_average
                    })
                })

                this.setState({
                    results:data.data
                })

            })

            this.setState({
                listData: ldd
            })
    }

    render(){
        return(
            <div>
                <div style={{marginTop: "5%", marginRight: '10%', marginLeft: '10%'}}>
                    <List
                        itemLayout="vertical"
                        size="large"
                        pagination={{
                        onChange: page => {
                            console.log(page);
                        },
                        pageSize: 5,
                        }}
                        dataSource={this.state.listData}
                        footer={
                        <div>
                            <b>ant design</b> footer part
                        </div>
                        }

                        renderItem={item => (

                        <List.Item
                            key={item.title}
                            // actions={[
                            // <IconText type="star-o" text="156" key="list-vertical-star-o" />,
                            // <IconText type="like-o" text="156" key="list-vertical-like-o" />,
                            // <IconText type="message" text="2" key="list-vertical-message" />,
                            // ]}
                            extra={
                            <img
                                width={175}
                                alt="logo"
                                src={item.image}
                            />

                            }

                        >

                            <List.Item.Meta
                            // avatar={<Avatar src={item.avatar} />}
                            title={

                                <div>
                                    {item.title}<br/>{item.release_date}
                                    {/* <Circle percent="50" style={{width:"50px", marginLeft:"20px"}} strokeWidth="15" strokeColor="#D3D3D3" /> */}
                                    <Circle_c 
                                        vote_average={item.vote_average}/>
                                    <div style={{marginLeft: "20px"}}>{item.vote_average}</div>
                                </div>
                            }
                            description={item.description}
                            />
                            <div><a href="">more info</a></div>
                        </List.Item>
                        )}
                    />
                </div>
            </div>
        )
    }
}

export default ListView
